import {Injectable} from '@angular/core'
import {environment} from '../../../environments/enviroment'
import {HttpClient, HttpParams} from '@angular/common/http'
import {catchError, Observable, throwError} from 'rxjs'
import Swal from 'sweetalert2'
import {Response} from '../models'

@Injectable()
export class ApiService {
  constructor(
    private readonly http: HttpClient
  ) {
  }

  private showErrors(error: Response<string>): Observable<never> {
    Swal.fire({
      title: 'Ocurrio un error',
      text: error.message,
      icon: 'error'
    }).then().catch(() => {
    })

    return throwError(() => error.message)
  }

  private showMessage(response: Response<any>): void {
    Swal.fire({
      title: 'Operacion exitosa',
      text: response.message,
      icon: 'error'
    }).then().catch(() => {
    })
  }

  get<T>(path: string, params: HttpParams = new HttpParams()): Observable<T> {
    return this.http
      .get<Response<T>>(`${environment.api_url}${path}`, {params})
      .subscribe({
        next: (response) => {
          
        },
        error: (error: Response<string>) => {
          this.showErrors(error)
        }
      })
  }

  put<T>(path: string, body: Object = {}): Observable<Response<T>> {
    return this.http.put<Response<T>>(
      `${environment.api_url}${path}`,
      JSON.stringify(body)
    ).pipe(catchError(this.showErrors))
  }

  post<T>(path: string, body: Object = {}): Observable<Response<T>> {
    return this.http.post<Response<T>>(
      `${environment.api_url}${path}`,
      JSON.stringify(body)
    ).pipe(catchError(this.showErrors))
  }

  delete(path: string): Observable<Response<string>> {
    return this.http.delete<Response<string>>(
      `${environment.api_url}${path}`
    ).pipe(catchError(this.showErrors))
  }
}
