import {Component, OnInit} from '@angular/core';
import {UsuarioService} from "../../core";
import {ActivatedRoute, Router} from "@angular/router";
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent implements OnInit {

  authType: string = ''
  title: string = ''
  authForm: FormGroup
  isSubmitting = false;

  constructor(
    private readonly route: ActivatedRoute,
    private readonly router: Router,
    private readonly usuarioService: UsuarioService,
    private readonly fb: FormBuilder
  ) {
    this.authForm = this.fb.group({
      'correo': ['', Validators.required, Validators.email],
      'password': ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.route.url.subscribe(data => {
      this.authType = data[data.length - 1].path
      this.title = this.authType === "login" ? "Login" : "Crear Cuenta"
      if (this.authType === "register") {
        this.authForm.controls['email'].setValidators([
          Validators.minLength(8),
          Validators.maxLength(50)
        ])

        this.authForm.controls['password'].setValidators([
          Validators.minLength(8),
          Validators.maxLength(32),
        ])

        this.authForm.addControl("nombreCompleto", new FormControl('', [
          Validators.minLength(8),
          Validators.maxLength(50)
        ]))

      }
    })
  }

  submitForm(): void {
    this.isSubmitting = true;
    const credentials = this.authForm.value
    this.usuarioService
      .attemptAuth(this.authType, credentials)
      .subscribe({
          next: (response) => {
            this.router.navigateByUrl('/')
          },
          error: (error) => this.isSubmitting = false
        }
      );
  }

}
<div class="min-vh-100 d-flex flex-column p-4 justify-content-center align-items-center background-primary">
  <div class="box background-secondary p-3 col-sm-3">
    <h2 class="text-center mb-1 text-primary-mat">{{title}}</h2>
    <p class="text-xs-center">
      <a [routerLink]="['/login']" *ngIf="authType == 'register'">Have an account?</a>
      <a [routerLink]="['/register']" *ngIf="authType == 'login'">Need an account?</a>
    </p>
    <form class="d-flex flex-column mt-4">
      <mat-form-field>
        <mat-label>Nombre Completo</mat-label>
        <input matInput required>
      </mat-form-field>
      <mat-form-field>
        <mat-label>Correo</mat-label>
        <input matInput required>
      </mat-form-field>
      <mat-form-field>
        <mat-label>Contraseña</mat-label>
        <input matInput required>
      </mat-form-field>

      <button class="text-accent-mat" type="submit" mat-raised-button color="warn">Crear Cuenta</button>
    </form>
  </div>
</div>
